import React, { Component } from 'react';
import RNPickerSelect from 'react-native-picker-select';
import { StyleSheet, ScrollView, View, Image, Text, TouchableOpacity, TextInput, Alert } from 'react-native';
import { Feather } from '@expo/vector-icons';
import Logo from '../../../assets/logo.png';
import Gas1 from '../../../assets/entrega.png';
import AsyncStorage from '@react-native-async-storage/async-storage';
import axios from 'axios';

const corFundo =      '#E5E6E8';
const corPadrao =     '#0B0D88';             // "O seu Gás em casa"
const corDestaque =   '#00C1A4';
const corSecundaria = '#082D95';

export default class confirmarPedido extends Component {

  constructor(props) {
    super(props);
    this.state = {
    flagDinheiro: null,
    troco: "00",
    nome: "",
    valor: 0,
    endereco: '',
    pagamento: '',
    troco: '',
    };
  }

  componentDidMount = async () => {
    await this.setState({valor: await AsyncStorage.getItem('resultado')})
  }

enviaValores = async () => {
  
  var resultado = 0;
  var var_email = await AsyncStorage.getItem('2email') 
  var var_valor = await AsyncStorage.getItem('resultado') 
  var botijao1 = await AsyncStorage.getItem('numBotijao1')
  var botijao2 = await AsyncStorage.getItem('numBotijao2')
  var botijao3 = await AsyncStorage.getItem('numBotijao3')
  var botijao4 = await AsyncStorage.getItem('numBotijao4')
  var botijao5 = await AsyncStorage.getItem('numBotijao5')

  this.setState({nome: var_email})

  if(this.state.troco == ""){
    this.setState({troco:"00"})
  }

  var valores = [
    [
      "nome",
      this.state.nome
    ],

    [
      "botijao1",
      botijao1
    ],

    [
      "botijao2",
      botijao2
    ],

    [
      "botijao3",
      botijao3
    ],

    [
      "botijao4",
      botijao4
    ],

    [
      "botijao5",
      botijao5
    ],

    [
      "valor",
      var_valor
    ],

    [
      "endereco",
      this.state.endereco
    ],

    [
      "pagamento",
      this.state.pagamento
    ],

    [
      "troco",
      this.state.troco
    ],

  ]
  
  console.log(valores)
  
  try{
    await axios.post('http://produto-gas.herokuapp.com/api/pedido',{valores})
    .then(function (response) {
      resultado = response.data
      console.log(resultado);
    });
  }

  catch (e) { //esse aqui retorna se der erro
    console.log(e)
  }
  
  if(resultado == "Pedido Cadastrado com sucesso") {
    Alert.alert("Sucesso!", "O seu pedido já foi registrado!");
  }
  
  else {
    Alert.alert("Erro!", "Houve algum problema em registrar seu pedido, tente novamente mais tarde.");
  }

  this.props.navigation.navigate('Produtos');
} 

  pedeTroco = (value) =>  {
    this.setState({flagDinheiro : null})
    if(value == 'dinheiro') {
      this.setState({flagDinheiro : 1})
    } else {
      this.setState({troco : "00"})
    }
    this.setState({pagamento : value})
  }


    render() {
        const { goBack } = this.props.navigation;
        
        return(
          <ScrollView style={styles.container}>
          <View>
          
            <View style={styles.container}>
              <TouchableOpacity 
                style = {styles.seta}
                onPress = {()=> goBack()}>
                  <Feather name="chevron-left" size={38} color={ corPadrao } />
              </TouchableOpacity>
              <Image 
                source={Logo} 
                style={styles.logo} />
              <Text style={styles.subTitulo}>O seu Gás em casa</Text>
              <Text style={styles.titulo}>HORÁRIO E LOCAL DE ATENDIMENTO:</Text>
              <Text style={styles.titulo2}>Seg - Sáb das 8h às 19h | Dom das 8h às 12h</Text>
              <Text style={styles.titulo2}>Uberlândia - MG</Text>

              <View style={styles.card}>
              <Text style={styles.cardTitulo}>Confirmar pedido:</Text>
              <View style={styles.cardContent}>
                <Image source={Gas1} 
                style={styles.gas} />
                <View style={styles.teste}>
                <Text style={styles.cardPrice}>R$ {this.state.valor}</Text>
                <TextInput 
                multiline
                numberOfLines={2}
                value={this.state.endereco}
                style = {styles.inputEndereco}
                placeholder = 'Endereço de entrega (Rua e número)'
                onChangeText = {texto => this.setState({endereco : texto})} 
                />
                <Text style={styles.titulo}>             </Text>
                <Text style={styles.titulo}>Selecione seu bairro</Text>
                <View style={styles.pickerContainer}>
                <RNPickerSelect
                  style={{	inputAndroid: {
                    color: '#aba',
                    justifyContent: 'center',
                    textAlign: 'center',
                    paddingHorizontal: 10,
                    paddingVertical: 8,
                    fontSize: 18,
                  },

                  }}
                  useNativeAndroidPickerStyle={false}
                  onValueChange={(value) => this.setState({bairro : (value)})} 
                  placeholder={{label: 'Bairro', value: null}}
                  items={[
                      { label: 'Bairro 1', value: 'bairro' },
                      { label: 'Bairro 2', value: 'bairro' },
                      { label: 'Bairro 3', value: 'bairro' },
                      { label: 'Bairro 4', value: 'bairro' },
                      { label: 'Bairro 5', value: 'bairro' },
                  ]}
                  
                />
                </View>
                </View>
              </View>
              </View>
              <Text style={styles.titulo}>Qual será o método de pagamento?</Text>
              <View style={styles.pickerContainer}>
              <RNPickerSelect
                style={{	inputAndroid: {
                  color: '#aba',
                  justifyContent: 'center',
                  textAlign: 'center',
                  paddingHorizontal: 10,
                  paddingVertical: 8,
                  fontSize: 18,
                },

                }}
                useNativeAndroidPickerStyle={false}
                onValueChange={(value) => this.pedeTroco(value)}
                placeholder={{label: 'Método de pagamento', value: null}}
                items={[
                    { label: 'Cartão de crédito', value: 'credito' },
                    { label: 'Cartão de débito', value: 'debito' },
                    { label: 'Dinheiro', value: 'dinheiro' },
                ]}
                
              />
              </View>
              <View style={{flexDirection:"row", alignSelf:"center", marginBottom: 20}}>
              {this.state.flagDinheiro && <Text style={{textAlignVertical: "center", color:"#00c1a4", fontSize:20, fontWeight:"bold", marginLeft:"auto"}}>Troco para R$ </Text>}
              {this.state.flagDinheiro && <TextInput 
                value={this.state.troco}
                style = {{     
                width: 'auto',
                padding: 8,
                borderColor: corSecundaria,
                borderWidth: 1.5,
                borderRadius: 3,}}
                keyboardType= 'numeric'
                placeholder = '  '
                onChangeText = {texto => this.setState({troco : texto})} 
              />}
              
              </View> 
              <TouchableOpacity 
                style = {styles.button} 
                onPress = {this.enviaValores}>
                <Text style={{color: 'white'}}>CONFIRMAR</Text>
              </TouchableOpacity>
              <Text style={styles.titulo}>             </Text>
              <Text style={styles.titulo}>             </Text>
              <Text style={styles.titulo}>             </Text>
              <Text style={styles.titulo}>             </Text>
              <Text style={styles.titulo}>             </Text>
              <Text style={styles.titulo}>             </Text>
              <Text style={styles.titulo}>             </Text>
            </View>
            
          </View>
          </ScrollView>
        );
    }
}

const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: corFundo,
    },

    input: { //Caixa do Formulário
      marginLeft: 'auto',
      marginRight: 'auto',
      marginBottom: 15,
      width: '50%',
      padding: 8,
      borderColor: corSecundaria,
      borderWidth: 1.5,
      borderRadius: 3,
    },

    scrollView: {
      height: '100%',
      width: '100%',
      alignSelf: 'center',
      padding: 0,
    },

    contentContainer: {
      height: '100%',
      width: '100%',
      justifyContent: 'center',
      alignItems: 'center',
